import ShoppingContext from "./shopping/ShoppingContext";
import React, { use, useContext } from "react";
import { Link } from "react-router-dom";
import Checkout from "./Checkout";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "../axios";
import "./Payment.css";

const Payment = () => {
  const shoppingContext = useContext(ShoppingContext);
  const { basket, user, getBasketTotal } = shoppingContext;

  const [succeeded, setSucceeded] = useState(false);
  const [processing, setProcessing] = useState("");
  const [error, setError] = useState(null);
  const [disabled, setDisabled] = useState(false);
  const [clientSecret, setClientSecret] = useState(true);

  useEffect(() => {
    // Generate the special stripe secret which allows us to charge a customer
    const getClientSecret = async () => {
      const response = await axios({
        method: "post",
        url: `/payments/create?total=${getBasketTotal(basket) * 100}`,
      });
      setClientSecret(response.data.clientSecret);
    };
    getClientSecret();
  }, [basket]);

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  const handleChange = (event) => {};

  const formatCurrency = (value) => {
    return new Intl.NumberFormat("en-ZA", {
      style: "currency",
      currency: "ZAR", // South African Rand
    }).format(value);
  };

  return (
    <div className="payments">
      <div class="payment_container">
        <h1>
          Checkout <Link to="/final-checkout">{basket?.length}</Link> Items
        </h1>
        <div class="payment_section">
          <div class="paymeny_title">
            <h3>Delivery Address</h3>
          </div>
          <div class="payment_address">
            <p>{user?.email}</p>
            <p>123 React Lane</p>
            <p>Cape Town, SA</p>
          </div>
        </div>

        <div class="payment_section">
          <div class="paymeny_title">
            <h3>Review items and delivery</h3>
          </div>
          <div class="payment_items">
            {basket.map((item) => (
              <Checkout
                key={item.id}
                id={item.id}
                title={item.title}
                image={item.image}
                price={500}
              />
            ))}
          </div>
        </div>
        <div class="payment_section">
          <div class="paymeny_title">
            <h3>Payment Method</h3>
            <div class="payment_details">{/* Stripe code will go here*/}</div>
            <form onSubmit={handleSubmit}>
              <CardElement onChange={handleChange} />
              <div class="payment_price_container"></div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Payment;
